mapper.select:
SELECT count(*) 
	FROM item
		WHERE re_manager_idx = #{manager_idx}

SELECT count(*) 
	FROM follow 
		WHERE rf_member_idx= #{member_idx}
		AND rf_manager_idx = #{manager_idx};

mapper.delete:
DELETE 
	FROM follow
	WHERE rf_member_idx = #{member_idx}
	AND
		  rf_manager_idx = #{manager_idx}
