mapper.update:
UPDATE board
	SET
		board_subject = #{board_subject},
		board_content = #{board_content}
	WHERE
		board_idx = #{board_idx}

UPDATE file_board
	SET
	     RF_BOARD_IDX = #{board_idx}
		 	 ,file1 = #{file1}
			, file2 = #{file2}
	WHERE
		rf_board_idx = #{board_idx}

UPDATE sell_detail
	SET
	     sell_review_status = "Y"
	WHERE
			rf_sell_idx = #{sell_idx}
		and rf_item_idx = #{item_idx}
