mapper.select:
SELECT * 
	FROM sell
	WHERE rf_member_idx = #{member_idx}

		 AND TIMESTAMPDIFF(month , sysdate(), SELL_DATE)>-#{period}

	ORDER BY sell_date DESC
	LIMIT #{startRow}, #{listLimit}

SELECT count(*) 
	FROM sell
	WHERE rf_member_idx = #{member_idx}

		 AND TIMESTAMPDIFF(month , sysdate(), SELL_DATE)>-#{period}

SELECT * 
	FROM sell 
	WHERE rf_member_idx = #{member_idx} 
		  AND 
		  sell_order_number = #{sell_order_number};
